<style scoped lang="scss">
.drawer-button {
  position: relative;
  display: flex;
  align-items: center;
  justify-content: flex-start;
  width: 100%;
  padding: 1.6rem 0 1.6rem 3.6rem;
  gap: 1.2rem;
  transition: background-color 300ms 100ms ease;

  &:hover {
    background-color: $primary-2;
  }

  &__mark {
    position: absolute;
    left: -.4rem;
    width: .4rem;
    height: 2.4rem;
    border-top-right-radius: .4rem;
    border-bottom-right-radius: .4rem;
    background-color: $secondary-1;
    transition: left 200ms ease;

    &--active {
      left: 0;
    }
  }

  &__icon {
    height: 2.4rem;
  }

  &__title {
    flex-shrink: 0;
    font-size: 1.2rem;
    font-weight: 500;
    opacity: 0;
    transition: opacity 400ms 100ms ease;

    &--active {
      opacity: 1;
    }
  }
}
</style>
